            <br />
            <label htmlFor="description">
                Description<br />
                <input type="textarea" id="description" name="description" placeholder="Brief description of the deck"/>
            </label>
            <div className="px-3">
                <div>
                    <Link to={`/decks/${deckId}`} className="btn btn-secondary m-2">View</Link>
                    <Link to={`/decks/${deckId}/study`} className="btn btn-primary">Study</Link>
                </div>
                <div>
                    <button name="delete" className="btn btn-danger">Delete</button>
