import React from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck, updateDeck} from "../../utils/api";
import { PropTypes } from "prop-types";

function DeckList({ id, name, description, cards, updateDecks }) {
  const history = useHistory();

  const handleDelete = async () => {
    if (window.confirm("Delete this deck? \n \n \n You will not be able to recover it.")) {
      await deleteDeck(id);
      updateDecks(-1);
      history.push("/");
    }
  };

  return (
    <li className="card m-2" key={deck.id}>
      <div className="card-body" style={{display: "flex", justifyContent:"space-between"}}>
        <h4 className="card-title">{deck.name}</h4>
        <p>{deck.cards.length} cards</p>
      </div>
      <div className="card-body">
        <p className="card-text">{deck.description}</p>
      </div>
      <div className="px-3" style={{display: 'flex', justifyContent: 'space-between'}}>
        <div>
          <Link to={`/decks/${deck.id}`} className="btn btn-secondary m-2">View</Link>
          <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
        </div>
        <div>
          <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
        </div>
      </div>
    </li>
  )
}

export default DeckList;
