import React from "react";
import { useHistory, Link, useParams } from "react-router-dom";

function StudyCard({cards}) {
  const initialCardState = { flipped: false, currentCard: 0 };
  const [session, setSession] = useState(initialCardState);
  const history = useHistory();
  const { deckId } = useParams();

  // loop through the cards array then prompt restart
  const handleNext = () => {
    if (session.currentCard < cards.length -1) {
      setSession({
        ...session,
        currentCard: session.currentCard + 1,
        flipped: false,
        });
    } else {
      window.confirm(
        "Restart cards? \n \n \n Click 'cancel' to return to the home page."
        ) ? setSession(initialCardState) : history.push("/");
    }
  };
  // if more than 3 cards, display cards
  if (cards.length > 2) {
    return (
      <div>
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">
              Card {session.currentCard + 1} of {cards.length}
            </h5>
            <p className="card-text">{
              session.flipped 
              ? cards[session.currentCard].back 
              : cards[session.currentCard].front}</p>
            <button className="btn btn-secondary mr-2"
            onClick={() => setSession({...session, flipped: true})}>Flip
            </button>
            {
            session.flipped && 
            (<button className="btn btn-primary" onClick={handleNext}>Next</button>
            )}
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div>
        <h2>Not enough cards</h2>
        <p>You need a minimum of 3 cards to study. There are {cards.length} cards in this deck.</p>
        <Link to={`/decks/${declId}/cards/new`} className="btn btn-primary">
          + Add Cards
        </Link>
      </div>
    );
  }
}

export default StudyCard;
