import React, { useState, useEffect } from "react";
import { useParams, Link, useHistory } from "react-router-dom";
import { deleteDeck, readDeck, listCards } from "../../utils/api";
import CardList from "../Deck/CardList";
import { PropTypes } from "prop-types";

function Deck({ updateDecks }) {
  const [deck, setDeck] = useState({});
  const [cards, setCards] = useState(0);
  const { deckId } = useParams();
  const history = useHistory();

  const handleDelete = async () => {
    const toDelete = window.confirm(
      "Delete this deck? \n \n \n You will not be able to recover it."
    );
    if (toDelete) {
      await deleteDeck(deck.id);
      updateDecks(-1);
      history.push("/");
    }
  };

  const updateCards = (val) => {
    setCards(() => cards + val);
  };

  // console.log(deckId);
  useEffect(() => {
    const abortController = new AbortController();
    async function loadDeck() {
        const response = await readDeck(deckId, abortController.signal);
        setDeck(() => response);
    }
    loadDeck();
    return () => {
      abortController.abort();
    }
  }, [deckId]);

  useEffect(() => {
    setCards([]);
      const abortController = new AbortController();
      async function loadCards() {
      try {
        const response = await listCards(deckId, abortController.signal);
        setCards(response);
      } catch (error) {
        if (error.name !== 'AbortError') throw error;
      }
      }
      loadCards();
      return () => {
        abortController.abort();
      }
  }, [deckId]);


  return (
    <div>
      <div className="mb-4">
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item"><Link to="/">Home</Link></li>
            <li className="breadcrumb-item active" aria-current="page">{deck.name}</li>
          </ol>
        </nav>
        <h2>{deck.name}</h2>
        <p>{deck.description}</p>
        <div style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>
            <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">Edit</Link>
            <Link to={`/decks/${deck.id}/study`} className="btn btn-primary ml-2">Study</Link>
            <Link to={`/decks/${deck.id}/cards/new`} className="btn btn-primary ml-2">+ Add Cards</Link>
          </div>
          <div>
          <button className="btn btn-danger" onClick={handleDelete}>&#x1f5d1;</button>
          </div>
        </div>
      </div>
      <h1>Cards</h1>    
      <div>
        {cards.map(({ id, front, back }) => (
        <CardList id={id} front={front} back={back} updateCards={updateCards}/>
        ))}
      </div>        
    </div>
  );
}

export default Deck;

Deck.propTypes = {
  updateDecks: PropTypes.func,
}
