import React from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteCard } from "../../utils/api";
import { PropTypes } from "prop-types";

function CardList({ id, front, back, deckId}) {
  const history = useHistory();
  const handleDelete = async () => {
    if (window.confirm("Delete this card? \n \n \n You will not be able to recover it.")) {
      await deleteCard(id);
      history.push("/");
    }
  };

  return (
    <div className="card mb-3">
      <div className="card-body">
        <p className="card-text">{front}</p>
        <p className="card-text">{back}</p>
      </div>
      <div className="px-3 mb-2" style={{display: 'flex', justifyContent: 'right'}}>
        <div>
          <Link to={`/decks/${deckId}/cards/${id}/edit`} className="btn btn-primary mr-2"><i className="bi bi-journal-bookmark-fill"></i>Edit</Link>
        </div>
        <div>
          <button className="btn btn-danger" onClick={handleDelete}>&#x1f5d1;<i className="bi bi-trash"></i></button>
        </div>
      </div>
    </div>  
  );
}

CardList.propTypes = {
  id: PropTypes.number,
  front: PropTypes.string,
  back: PropTypes.string,
};

export default CardList;
