import React, { useState, Fragment } from "react";
import { Link } from "react-router-dom";
import { createDeck } from "../utils/api";

function NewDeck(){
    const initialDeckState = {
        name: "",
        description: "",
    };
    const [newDeckData, setNewDeckData] = useState({initialDeckState});
    const handleChange = ({ target }) => {
        event.preventDefault();
        setNewDeckData({
            ...newDeckData,
            [target.name]: target.value,
        });
    };

    async function handleSubmit (event) {
        event.preventDefault();
        const response = await createDeck(newDeckData);
        setNewDeckData({...initialDeckState});
        history.pushState(`/deck/${response.id}`);
    }

    return (
        <Fragment>
            <nav aria-label="breadcrumb">
                <ol class="breadcrumb">
                    <li class="breadcrumb-item"><Link to="/">Home</Link></li>
                    <li class="breadcrumb-item active" aria-current="page">Create Deck</li>
                </ol>
            </nav>
            <form onSubmit={handleSubmit}>
                <h1>Create Deck</h1>
                <label htmlFor="name" style={{display:"block"}}>
                    Name
                </label>   
                <input type="text" 
                    id="name" 
                    name="name" 
                    placeholder="Deck Name" 
                    class="form-control" 
                    onChange={handleChange}
                    style={{width: "100%"}} 
                     />                
                <label htmlFor="description">
                    Description
                </label>
                <textarea 
                    id="description" 
                    name="description" 
                    placeholder="Brief description of the deck" class="form-control" 
                    onChange={handleChange}
                    style={{width: "100%", height: "150px"}}  
                     />
                <div className="mt-3">
                    <Link to={`/`} className="btn btn-secondary mr-2">Cancel</Link>
                    <button className="btn btn-primary" type="submit">Submit</button>
                </div>
            </form>
        </Fragment>
    )
};

export default NewDeck;
