import React, { useState, useEffect } from "react";
import { useParams, Link, Route, Switch } from "react-router-dom";
import { listCards, readDeck } from "../../utils/api";

function Deck() {
    const [deck, setDeck] = useState({});
    const [cards, setCards] = useState([]);
    const { deckId } = useParams();
    // console.log(deckId);
    useEffect(() => {
        setDeck({});
        const abortController = new AbortController();
        async function loadDeck() {
            try {
                const response = await readDeck(deckId, abortController.signal);
                setDeck({...response});
            } catch (error) {
                if (error.name !== 'AbortError') throw error;
            }
        }
        loadDeck();
        return () => {
            abortController.abort();
        }
    }, [deckId]);

    useEffect(() => {
        setCards([]);
        const abortController = new AbortController();
        async function loadCards() {
            try {
                const response = await listCards(deckId, abortController.signal);
                setCards(response);
            } catch (error) {
                if (error.name !== 'AbortError') throw error;
            }
        }
        loadCards();
        return () => {
            abortController.abort();
        }
    }, [deckId]);


    return (
        <div>
            <div className="mb-4">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb">
                        <li className="breadcrumb-item"><Link to="/">Home</Link></li>
                        <li className="breadcrumb-item active" aria-current="page">{deck.name}</li>
                    </ol>
                </nav>
                <h2>{deck.name}</h2>
                <p>{deck.description}</p>
                <div style={{display: 'flex', justifyContent: 'space-between'}}>
                    <div>
                        <Link to={`/decks/${deck.id}/edit`} className="btn btn-secondary">Edit</Link>
                        <Link to={`/decks/${deck.id}/study`} className="btn btn-primary ml-2">Study</Link>
                        <Link to={`/decks/${deck.id}/cards/new`} className="btn btn-primary ml-2">+ Add Cards</Link>
                    </div>
                    <div>
                        <button className="btn btn-danger">&#x1f5d1;</button>
                    </div>
                </div>
            </div>    
            <div>
              <h1>Cards</h1>
              <CardList cards={cards}/>
            </div>        
        </div>
    );
}

export default Deck;
