import React from "react";
import { Link, useHistory } from "react-router-dom";
import { deleteCard } from "../../utils/api";
import { PropTypes } from "prop-types";

function CardList({ id, front, back}) {
  const history = useHistory();

  const handleDelete = async () => {
    if (window.confirm("Delete this deck? \n \n \n You will not be able to recover it.")) {
      await deleteCard(id);
      updateDecks(-1);
      history.push("/");
    }
  };

  return (
    <div className="card mb-3">
      <div className="card-body" style={{display: "flex", justifyContent:"space-between"}}>
        <h4 className="card-title">{name}</h4>
        <p>{cards.length} cards</p>
      </div>
      <div className="card-body">
        <p className="card-text">{description}</p>
      </div>
      <div className="px-3" style={{display: 'flex', justifyContent: 'space-between'}}>
        <div>
          <Link to={`/decks/${id}`} className="btn btn-secondary m-2"><i className="bi bi-eye-fill"></i>View</Link>
          <Link to={`/decks/${id}/study`} className="btn btn-primary"><i className="bi bi-journal-bookmark-fill"></i>Study</Link>
        </div>
        <div>
          <button className="btn btn-danger" onClick={handleDelete}>{" "}<i className="bi bi-trash"></i></button>
        </div>
      </div>
    </div>  
  );
}

CardList.propTypes = {
  id: PropTypes.number,
  front: PropTypes.string,
  back: PropTypes.string,
};

export default CardList;
