import React, { useState, useEffect } from "react";
import { useParams, Link, Route, Switch } from "react-router-dom";
import { listCards, readDeck } from "../utils/api";

function Deck() {
    const [deck, setDeck] = useState({});
    const [cards, setCards] = useState([]);
    const { deckId } = useParams();
    // console.log(deckId);
    useEffect(() => {
        setDeck({});
        const abortController = new AbortController();
        async function loadDeck() {
            try {
                const response = await readDeck(deckId, abortController.signal);
                setDeck({...response});
            } catch (error) {
                if (error.name !== 'AbortError') throw error;
            }
        }
        loadDeck();
        return () => {
            abortController.abort();
        }
    }, [deckId]);

    useEffect(() => {
        setCards([]);
        const abortController = new AbortController();
        async function loadCards() {
            try {
                const response = await listCards(deckId, abortController.signal);
                setCards(response);
            } catch (error) {
                if (error.name !== 'AbortError') throw error;
            }
        }
        loadCards();
        return () => {
            abortController.abort();
        }
    }, [deckId]);


    return (
        <div>
            <div>
                <nav aria-label="breadcrumb">
                    <ol class="breadcrumb">
                        <li class="breadcrumb-item"><Link to="/">Home</Link></li>
                        <li class="breadcrumb-item active" aria-current="page">{deck.name}</li>
                    </ol>
                </nav>
                <h2>{deck.name}</h2>
                <p>{deck.description}</p>
                <div style={{display: 'flex', justifyContent: 'space-between'}}>
                    <div>
                        <button className="btn btn-secondary">Edit</button>
                        <button className="btn btn-primary ml-2">Study</button>
                        <button className="btn btn-primary ml-2">+ Add Cards</button>
                    </div>
                    <div>
                        <button className="btn btn-danger">&#x1f5d1;</button>
                    </div>
                </div>
            </div>    
            <div>
                <h1>Cards</h1>
                <ul>
                    {cards.map((card) => {
                        return (
                            <li className="card m-2" key={card.id}>
                                <div className="card-body" style={{display: "flex", justifyContent:"space-between"}}>
                                {card.front}<br />{card.back}
                                </div>
                                <div className="card-body">
                                    <p className="card-text">{deck.description}</p>
                                </div>
                                <div className="px-3" style={{display: 'flex', justifyContent: 'space-between'}}>
                                    <div>
                                        <Link to={`/decks/${deck.id}`} className="btn btn-secondary m-2">View</Link>
                                        <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                                    </div>
                                    <div>
                                        <button name="delete" className="btn btn-danger">Delete</button>
                                    </div>
                                </div>
                            </li>
                        )
                    })}
                </ul>
            </div>        
        </div>
    );
}

export default Deck;
