import React, { useState, useEffect} from "react";
import { Link } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api"

function Decks({decks, setDecks}) {

    useEffect(() => {
        setDecks([]);
        const abortController = new AbortController();
        async function loadDecks() {
            try {
                const response = await listDecks(abortController.signal);
                setDecks(response);
            } catch (error) {
                throw error;
            }
        }
        loadDecks();
        return () => {
            // console.log("Clean Up");
            abortController.abort();
        }
    }, [setDecks]);

    async function handleClick (event) {
        event.preventDefault();
        const abortController = new AbortController();
        if (window.confirm("Delete this deck?")) {
            window.open("index.html")
            await deleteDeck(event.target.value, abortController.signal)
        }
    }

    return (
        <div>
            <ul>
                {decks.map((deck) => {
                    return (
                        <li className="card m-2" key={deck.id}>
                            <div className="card-body" style={{display: "flex", justifyContent:"space-between"}}>
                                <h4 className="card-title">{deck.name}</h4>
                                <p>{deck.cards.length} cards</p>
                            </div>
                            <div className="card-body">
                                <p className="card-text">{deck.description}</p>
                            </div>
                            <div className="px-3" style={{display: 'flex', justifyContent: 'space-between'}}>
                                <div>
                                    <Link to={`/decks/${deck.id}`} className="btn btn-secondary m-2">View</Link>
                                    <Link to={`/decks/${deck.id}/study`} className="btn btn-primary">Study</Link>
                                </div>
                                <div>
                                    <button name="delete" className="btn btn-danger" onClick={handleClick}>Delete</button>
                                </div>
                            </div>
                        </li>
                    )
                })}
            </ul>
        </div>
    )
}

export default Decks;
